			 getEachDiffHeight = getEachdiffObj.diffvar_len * geteachlineHeight + "px";
			 gutterMarginHtml += "<div class='acediff-marginelem' style='top:" + getEachDiffTop  + ";height:" + getEachDiffHeight + ";' leftstartLine=" + getEachdiffObj.leftStartLine +" leftendLine=" + getEachdiffObj.leftEndLine + " rightstartLine=" + getEachdiffObj.rightStartLine + " rightendLine=" + getEachdiffObj.rightEndLine + " diffside=both"  + "></div>"; 
