	if (number > this.terminalWidth - this.cursorX) {
		number = this.terminalWidth - this.cursorX;
	}
	this.scrollRegion(this.cursorX + number, this.cursorY, this.terminalWidth
			- this.cursorX - number, 1, -number, 0, this.color, this.style);
