		if(!iconClass){
			iconClass = data.icon;
		}
		if(data.iconText){
			iconClass = (iconClass? iconClass+" " : '') + 'zc-zmenu-icon-textable'; // No I18N
		}
		return iconClass ? iconClass: ""; // No I18N
