		var data = ZComponents.getMetadata(menuItem);
		if(data.contextual){
			menuItem.addClass("zc-zmenu-item-contextual"); // No I18N
			var base = this;
			menuItem.on("contextmenu."+this.widgetName, function(ev){ // No I18N
				ev.preventDefault();
				base._showSubmenu(menuItem, {
					"forcePosition": true, // No I18N
					"position": "after-pointer", // No I18N
					"event":ev // No I18N
				}, true,ev);	
				base._setFocus();
			});
		}
		var menu = this.getSubmenu(menuItem);
		if(!menu){
			return;
		}
