		if(typeof submenu === "string"){ // No I18N
			submenu = $(submenu);
			if(!submenu.length){
				return undefined;
			}
			var metadata = ZComponents.getMetadata(submenu);
			if(!submenu.data("zmenu")){ // No I18N
				submenu.zmenu(metadata.componentOptions ? metadata.componentOptions : metadata);
			}
			metadata.isExternalSubMenu = true;
		}
		if(submenu && submenu.length){
			submenu.data('parent',menuItem); // No I18N
			return submenu;
		}
		return (menuItem.children("ul:first")); // No I18N
