		groupMenu.addClass("zc-zmenu-group-base zc-group-menu "+(innerMenuType ? this._MENU_SCOPES[innerMenuType] : ""));	// No I18N
		var groupItems = $(groupMenu).children("li"); // No I18N
			groupName = menuItem.attr("id"), // No I18N
			base = this;
		groupItems.each(function(i){
			var item = $(groupItems[groupItems.length-i-1]);
			base._buildMenuItem(item, groupMenu);
			base._buildGroupItem(item, groupName);
		});
