	/* Fixing the height of the menu based on window height and init/uninit the scrolls accordingly  */
	ZMenu.prototype._fixMenuHeight = function(menu, options){
		var menuHeight = "auto";  // No I18N
		menu.data('container') ? menu.data('container').height(menuHeight) : menu.height(menuHeight); // No I18N
		menu.height(menuHeight);
		menu.removeClass("zc-zmenu-scroll"); // No I18N
		this.options.appendTo = (typeof this.options.appendTo === "string" && this.options.appendTo.indexOf("#") !== -1 ) ? $(this.options.appendTo) : (typeof this.options.appendTo !== "string") ? this.options.appendTo : $("#"+appendTo); // No I18N
		var positionAlterable = this.options.positionAlterable,
			height = menu.data("container") ? menu.data("container").height() : menu.height(), // No I18N
			top = menu.css('top').split('px')[0] * 1 - $(window).scrollTop(); // No I18N
			windowHeight = this.options.appendTo && this.options.appendTo.length ? this.options.appendTo.height() : ZComponents.windowObject.height,
			space = windowHeight - (height);
			if(menu.data("container")){ // No I18N
				top = menu.data("container").css("top").split("px")[0] * 1 - $(window).scrollTop(); // No I18N
			}
		top = top ? top : 0;
		if(top + height >= windowHeight && (space < 0) || !positionAlterable){
			menuHeight = windowHeight - top - 15;
		}
		if(menu.data('height')){
			menuHeight = menu.data('height'); // No I18N
		}else if(space > 0 && positionAlterable){
			menuHeight = height;
		}
		var scrollWidth = this._findScrollWidth(),
			menudata = ZComponents.getMetadata(menu);
			scroll = (menudata.isExternalSubMenu && menudata.componentOptions) ? menudata.componentOptions.scroll : this.options.scroll;
		if(height > menuHeight){
			if(menu.data('container')){
				var containerElementsHeight = menu.data('container').find('.zc-search-main').height() + menu.data('container').find('#morel').height(); // No I18N
				menu.data('container').height(menuHeight); // No I18N
				menu.height(menuHeight - containerElementsHeight - 15);
			}else{
				menu.height(menuHeight);
			}
			var oldHeight = menu.data("modifiedHeight");	// No I18N				
			menu.data("modifiedHeight", menuHeight); // No I18N
			scroll = scroll ? scroll: "default"; // No I18N
			if(scroll && scroll !== "none"){ // No I18N
				if(scroll === "slider"){ // No I18N
					if(oldHeight && oldHeight !== menuHeight){
						this._uninitSliders(menu);
					}					
					this._initSliders(menu);
				}else{
	    			// console.log("Before Adding Scroll Class : "+ menu.width());
                    menu.addClass("zc-zmenu-scroll");
	    			// console.log("After Adding Scroll Class : "+ menu.width());
                    // Floating menuItems margin fix
        			var scrollMenuWidth = menu.data("width")+(this._isFloatingMenu(menu) ? scrollWidth+1 : (scrollWidth));	// No I18N
        			// var scrollMenuWidth = menu.width()+(this._isFloatingMenu(menu) ? scrollWidth+1 : (scrollWidth));	// No I18N
	    			menu.width(scrollMenuWidth);
	    			// console.log("After setting Scroll Width :" +menu.width());
	    			// menu.width(menu.width()+20);
				}
			}
		}else{
			if(scroll && scroll !== "none"){ // No I18N
				menu.removeClass("zc-zmenu-scroll");
			}
		}
	}
	/* Initialize the sliders and displaying them */ 
