	/* Destroys the menu by removing binded events */
	ZMenu.prototype.destroy = function(menu){	
		var menu = menu ? menu : this.element;
		this._clearMenuTimeout();
		this._uninitSliders(menu);
		if(menu.data("usingJSON") || this.options.usingJSON){ // No I18N
			var submenuItems = menu.find("[submenuitem=true]"); // No I18N
			$.each(submenuItems,function(i,item){
				$(ZComponents.getMetadata($(item)).submenu).zmenu("destroy"); // No I18N
				$(ZComponents.getMetadata($(item)).submenu).remove(); 
			});
			menu.empty(); // Json menu contents are removed.
		}
		menu.off('click.'+this.widgetName).removeClass('zc-menu zc-list-box '+this._MENU_SCOPES[this.options.type]+' zc-zmenu-scroll').removeData('zmenu metadata').data('initialized',false); // No I18N
		menu.find('.zc-zmenu-item-contextual').off('.'+this.widgetName); // No I18N
		// Destroying split menu items
		var splitItems = menu.find('.zc-zmenu-splitmenu').removeClass('zc-zmenu-splitmenu');
		splitItems.find('.zc-splitmenu-list-lft').removeClass('zc-splitmenu-list-lft').unwrap().unwrap();
		menu.find('.zc-splitmenu-list-rht').remove();
		// Destroying group items
		var groupItems = menu.find('.zc-zmenu-group-wrapper').removeClass('zc-zmenu-group-wrapper');
		groupItems.find('ul').removeClass('zc-zmenu-group-base zc-group-menu').find("li:first").remove();
		groupItems.find('li').removeClass('zc-menu-group zc-menu-item zc-list-item zc-zmenu-group-item').empty();
		// Destroying separator items
		menu.find('.bdrline').removeClass('bdrline');
		menu.find('.zc-list-item').removeData('metadata').removeClass('zc-list-item zc-state-disabled').empty().removeAttr('data-label').unbind('mouseenter mouseleave'); // No I18N
		if(menu.data('container')){
			this._destroyContainer(menu);
		}
	}
